mapper.select:
SELECT A.*, M.nickname AS extra__writer, IFNULL(SUM(RP.point), 0) AS extra__sumReactionPoint, IFNULL(SUM(IF(RP.point > 0, RP.point, 0)), 0) AS extra__goodReactionPoint, IFNULL(SUM(IF(RP.point < 0, RP.point, 0)), 0) AS extra__badReactionPoint FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id LEFT JOIN reactionPoint AS RP ON A.id = RP.relId AND RP.relTypeCode = 'article' WHERE A.boardId = #{boardId}
AND title LIKE CONCAT('%', #{searchKeyword}, '%')
AND `body` LIKE CONCAT('%', #{searchKeyword}, '%')
AND title LIKE CONCAT('%', #{searchKeyword}, '%') OR `body` LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY A.id DESC LIMIT #{articleFrom}, #{articlesPerPage}

mapper.update:
UPDATE article
title = #{title}, `body` = #{body}, updateDate= NOW()
WHERE id = #{id};

UPDATE article
SET hitCount = hitCount + 1
WHERE id = #{id}

mapper.select:
SELECT hitCount
FROM article
WHERE id = #{id}
